listitem.programlisting:
#!/bin/sh ########################################################################## # # Prints the local weather to /var/run/update-motd/60-local-weather # for update-motd. # ##########################################################################
# Replace KINT with your local weather station. # Local stations can be found here: http://www.weather.gov/tg/siteloc.shtml
echo "" > /var/run/update-motd/60-local-weather weather -i KINT >> /var/run/update-motd/60-local-weather
